import React, { useState } from 'react';
import './App.css';

function App() {
  // State untuk menyimpan data pengguna
  const [userData, setUserData] = useState({ nama: 'Cheryl', umur: 18 });
  // State untuk menyimpan daftar tugas
  const [tasks, setTasks] = useState([]);
  // State untuk menyimpan tugas baru yang akan ditambahkan
  const [taskName, setTaskName] = useState('');

  // Fungsi untuk mengubah nama pengguna
  const changeName = () => {
    setUserData({ ...userData, nama: 'Pika', umur: 20 });
  };

  // Fungsi untuk mengubah nama pengguna berdasarkan input
  const changeUserName = (event) => {
    setUserData({ ...userData, nama: event.target.value });
  };

  // Fungsi untuk mengubah umur pengguna berdasarkan input
  const changeUserAge = (event) => {
    setUserData({ ...userData, umur: parseInt(event.target.value) });
  };

  // Fungsi untuk menambahkan tugas baru ke daftar tugas
  const addTask = () => {
    if (taskName.trim() !== '') {
      setTasks([...tasks, taskName]);
      setTaskName('');
    }
  };

  // Fungsi untuk menghapus tugas dari daftar tugas
  const removeTask = (index) => {
    const newTasks = [...tasks];
    newTasks.splice(index, 1);
    setTasks(newTasks);
  };

  return (
    <div className="App">
      <div className="header">State dan Props di Component React.js</div>

      {/* Bagian untuk data pengguna */}
      <div className="user-container">
        <h2>Data Pengguna</h2>
        <div className="data-item">
          <label>Nama:</label> 
          <input
            type="text"
            value={userData.nama}
            onChange={changeUserName}
          />
        </div>
        <div className="data-item">
          <label>Umur:</label> 
          <input
            type="number"
            value={userData.umur}
            onChange={changeUserAge}
          />
        </div>
        <button onClick={changeName}>Ubah Data Pengguna</button>
      </div>

      {/* Bagian untuk daftar tugas */}
      <div className="task-container">
        <h2>Daftar Tugas</h2>
        <div className="input-container">
          <input
            type="text"
            placeholder="Tambahkan tugas baru"
            value={taskName}
            onChange={(e) => setTaskName(e.target.value)}
          />
          <button onClick={addTask}>Tambahkan Tugas</button>
        </div>
        <ul>
          {tasks.map((task, index) => (
            <li key={index}>
              {task}
              <button onClick={() => removeTask(index)}>Hapus</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
