import React, { Component } from "react";

export default class ClassComponent extends Component {

    

    clickHandler() {
        console.log("Button is clicked in the class componenet");
    }

    render() {
        return(
            <div>
                <button onClick={this.clickHandler}>Class Button</button>
            </div>
        );
    }
}
