import React, { useState } from 'react';
import './App.css';

function App() {
  // State untuk menyimpan data pengguna
  const [userData, setUserData] = useState({ nama: '', umur: '' });

  // Fungsi untuk mengubah data pengguna
  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData((prevUserData) => ({
      ...prevUserData,
      [name]: value
    }));
  };

  // Fungsi untuk menyimpan data pengguna
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Data Pengguna:", userData);
  };

  return (
    <div className="App">
      <div className="header">State dan Props di Component React.js</div>
      <div className="user-container">
        <h2>Data Pengguna</h2>
        <form onSubmit={handleSubmit}>
          <div className="data-item">
            <label>Nama:</label> 
            <input
              type="text"
              name="nama"
              value={userData.nama}
              onChange={handleChange}
              placeholder="Masukkan nama pengguna"
            />
          </div>
          <div className="data-item">
            <label>Umur:</label> 
            <input
              type="number"
              name="umur"
              value={userData.umur}
              onChange={handleChange}
              placeholder="Masukkan umur pengguna"
            />
          </div>
          <button type="submit">Simpan Data Pengguna</button>
        </form>
      </div>
    </div>
  );
}

export default App;
