// Fungsi untuk mengubah warna latar belakang
function changeBackgroundColor(color) {
  document.body.style.backgroundColor = color;
}

// Fungsi untuk mengubah ukuran teks
function changeTextSize(size) {
  document.body.style.fontSize = size;
}

// Fungsi untuk membuat dan menambahkan tombol ke dalam body
function addButton(text, clickHandler) {
  var button = document.createElement("button");
  button.textContent = text;
  button.classList.add("button");
  button.onclick = clickHandler;
  document.body.appendChild(button);
}

// Menambahkan tombol untuk mengubah warna latar belakang
addButton("Blue Background", function() {
  changeBackgroundColor('lightblue');
});

addButton("Green Background", function() {
  changeBackgroundColor('lightgreen');
});

// Menambahkan tombol untuk mengubah ukuran teks
addButton("Normal Text Size", function() {
  changeTextSize('16px');
});

addButton("Large Text Size", function() {
  changeTextSize('20px');
});
